SET SQL_MODE="NO_AUTO_VALUE_ON_ZERO"; SET time_zone = "+00:00"; SET CHARACTER_SET_CLIENT=utf8mb4; SET CHARACTER_SET_RESULTS=utf8mb4; SET COLLATION_CONNECTION=utf8mb4_unicode_ci; CREATE TABLE IF NOT EXISTS `servers` ( `id` INT NOT NULL AUTO_INCREMENT, `name` VARCHAR(255) NOT NULL, `hostname` VARCHAR(255) UNIQUE NOT NULL COMMENT 'Server hostname or IP', `location` VARCHAR(100) NOT NULL COMMENT 'Physical location', `host_provider` VARCHAR(100) COMMENT 'Hosting provider name', `server_type` VARCHAR(100) COMMENT 'Server type (Web, DB, Mail, etc)', `api_key` VARCHAR(255) UNIQUE NOT NULL COMMENT 'API key for agent authentication', `status_check_timeout` INT DEFAULT 300 COMMENT 'Timeout in seconds before marking offline', `is_active` TINYINT DEFAULT 1, `created_at` TIMESTAMP DEFAULT CURRENT_TIMESTAMP, `updated_at` TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (`id`), UNIQUE KEY `uq_hostname` (`hostname`), UNIQUE KEY `uq_api_key` (`api_key`), KEY `idx_is_active` (`is_active`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci; INSERT INTO `servers` (`name`, `hostname`, `location`, `host_provider`, `server_type`, `api_key`) VALUES ('Web Server 01', 'web01.example.com', 'Jakarta', 'Provider A', 'Web Server', 'api_key_web01_unique_123456'), ('Database Server 01', 'db01.example.com', 'Jakarta', 'Provider A', 'Database', 'api_key_db01_unique_123456'), ('Mail Server 01', 'mail01.example.com', 'Jakarta', 'Provider B', 'Mail Server', 'api_key_mail01_unique_123456');